import React, { Component } from 'react';
import './App.css';
import ReactDOM from "react-dom";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showComponent: false,
      data: null,
      firstCity: "Moscow",
      secondCity: "London",
    };

  }
  componentDidMount() {
      // Call our fetch function below once the component mounts
    this.callBackendAPI()
      .then(res => this.setState({ data: res.express }))
      .catch(err => console.log(err));
  }
    // Fetches our GET route from the Express server. (Note the route we are fetching matches the GET route from server.js
  callBackendAPI = async () => {
    const response = await fetch('/backend');
    const body = await response.json();

    if (response.status !== 200) {
      throw Error(body.message) 
    }
    this.state.data = body;
    //console.log(this.data);
    return body;
  };

  _callBackendCity(event) {
    // const response = await fetch('/backend'); {/* тут надо передать параметры (2 города) */}
    // const body = await response.json();

    // if (response.status !== 200) {
    //   throw Error(body.message) 
    // }
    this.state.showComponent = true;
    this.state.firstCity = event.target.value;
    console.log(this.state.firstCity);

    //this.forceUpdate();
    //return body;
  }

  render() {
    return (
      <div className="App">
        <input type="text" id="name" onChange={this._callBackendCity.bind(this)} />
        <h3>{this.state.name}</h3>

        <p className="App-intro">{this.state.firstCity}</p>

        {/* <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <h1 className="App-title">Welcome to React</h1>
        </header> */}
      </div>

    );
  }
}

export default App;
