import React, { Component } from 'react';
import './App.css';
import ReactDOM from "react-dom";
import style from 'bootstrap/dist/css/bootstrap.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showComponent: false,
      data: null,
      firstCity: "",
      secondCity: "",
      resultWay: "",
      resultDist: "Result distance: ",
      cities: [],
      add: "",
      addRel: "",
    };
  }

  componentDidMount() {
    window.addEventListener('load', this.callInit);
  }

  callInit = async () => {
    var response = await fetch('/init')
    var body = await response.json();
    this.state.cities = body;

    var firstbox = document.getElementById('first');
    var secondbox = document.getElementById('second');

    for(var i = 0; i < this.state.cities.length; i++){
      var option = this.state.cities[i];
      console.log(typeof this.state.cities[i]);
      firstbox.options.add(new Option(option, i) );
      secondbox.options.add(new Option(option, i) );
    }
  };

  searchThis = async () => {
    const response = await fetch('/', {
    method: 'POST',
    headers: {
        'Content-Type': 'application/json'
    },
    body: JSON.stringify({
        cities: {
            firstCity: this.state.firstCity,
            secondCity: this.state.secondCity
        }
      })
    });
    const body = await response.json();
    this.state.resultDist = "Result distance: " + body + " km";
    this.forceUpdate();
  };

  search(event) {
    this.state.firstCity = this.state.cities[document.getElementById("first").value]; 
    this.state.secondCity = this.state.cities[document.getElementById("second").value]; 

    this.searchThis();    
    this.forceUpdate();
  }

  addCity = async () => {
    const response = await fetch('/add_city', {
    method: 'POST',
    headers: {
        'Content-Type': 'application/json'
    },
    body: JSON.stringify({
        name: this.state.add
      })
    });
    this.callInit();
    this.forceUpdate();
  }

  addText(event) {
    this.state.add = event.target.value;
    this.forceUpdate();
  }

  addRel = async () => {
    this.state.firstCity = this.state.cities[document.getElementById("first").value]; 
    this.state.secondCity = this.state.cities[document.getElementById("second").value]; 

    const response = await fetch('/add_rel', {
    method: 'POST',
    headers: {
        'Content-Type': 'application/json'
    },
    body: JSON.stringify({
        firstCity: this.state.firstCity,
        secondCity: this.state.secondCity,
        dist: this.state.addRel
      })
    });
  }

  addRelText(event) {
    this.state.addRel = event.target.value;
    this.forceUpdate();
  }

  render() {
    return (
      <div className="App">
        <div class="container">
          <div class="header clearfix">
            <nav>
              <ul class="nav nav-pills float-right">
                <li class="nav-item">
                  <a class="nav-link active" href="#">Поиск <span class="sr-only">(current)</span></a>
                </li>
                <li class="nav-item">
                  <a class="nav-link" href="#">РПЗ </a>
                </li>
              </ul>
            </nav>
            <h3 class="text-muted">Курсовой проект по базам данных</h3>
          </div>

          <div class="jumbotron">
            <h1 class="display-3">Исходный граф: </h1>
            <p class="lead">Cras justo odio, dapibus ac facilisis in, egestas eget quam. Fusce dapibus, tellus ac cursus commodo, tortor mauris condimentum nibh, ut fermentum massa justo sit amet risus.</p>
            <p><a class="btn btn-lg btn-success" href="#" role="button">Sign up today</a></p>
          </div>

          <div class="row marketing">
            <div class="col-lg-6">
              <p> From </p> <select id="first"></select> <p> to </p> <select id="second"></select>
              <br /><br /><button onClick={this.search.bind(this)}>Search</button>
              <p className="App-intro">{this.state.resultDist}</p>
            </div>

            <div class="col-lg-6">
              <h4>Subheading</h4>
              <p>Donec id elit non mi porta gravida at eget metus. Maecenas faucibus mollis interdum.</p>

              <h4>Subheading</h4>
              <p>Morbi leo risus, porta ac consectetur ac, vestibulum at eros. Cras mattis consectetur purus sit amet fermentum.</p>

              <h4>Subheading</h4>
              <p>Maecenas sed diam eget risus varius blandit sit amet non magna.</p>
            </div>
          </div>

          <footer class="footer">
            <p>&copy; Company 2017</p>
          </footer>

        </div>

       </div>

      // <div className="App">

      //   <span> From </span> <select id="first"></select> <span> to </span> <select id="second"></select>

      //   <br /><br /><button onClick={this.search.bind(this)}>Search</button>

      //   <br /><br /><input type="text" id="addCity" onChange={this.addText.bind(this)} />
      //   <button onClick={this.addCity.bind()}>Add city</button>

      //   <input type="text" id="addRel" onChange={this.addRelText.bind(this)} />
      //   <button onClick={this.addRel.bind()}>Add relation</button>

      //   <p className="App-intro">{this.state.resultDist}</p>

      //   {/* <header className="App-header">
      //     <img src={logo} className="App-logo" alt="logo" />
      //     <h1 className="App-title">Welcome to React</h1>
      //   </header> */}
      // </div>

    );
  }
}

export default App;
