import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {
  data = null;
  firstCity = "Moscow";
  secondCity = "London";

  componentDidMount() {
      // Call our fetch function below once the component mounts
    this.callBackendAPI()
      .then(res => this.setState({ data: res.express }))
      .catch(err => console.log(err));
  }
    // Fetches our GET route from the Express server. (Note the route we are fetching matches the GET route from server.js
  callBackendAPI = async () => {
    const response = await fetch('/backend');
    const body = await response.json();

    if (response.status !== 200) {
      throw Error(body.message) 
    }
    this.data = body;
    //console.log(this.data);
    return body;
  };

  callBackendCity = async () => {
    const response = await fetch('/backend'); {/* тут надо передать параметры (2 города) */}
    const body = await response.json();

    if (response.status !== 200) {
      throw Error(body.message) 
    }

    return body;
  };

  render() {
    return (
      <div className="App">
        <form onSubmit={this.handleSearchTermSubmit}>
          <input type="text" ref={(input) => this.firstCity = input} />
          <input type="text" ref={(input) => this.secondCity = input} />

          <button onClick={this.callBackendForCity}>City</button>
        </form>

        {/* <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <h1 className="App-title">Welcome to React</h1>
        </header> */}
        <p className="App-intro">{this.data}</p>
      </div>

    );
  }
}

export default App;
