import React, { Component } from 'react';
import './App.css';
import ReactDOM from "react-dom";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showComponent: false,
      data: null,
      firstCity: "",
      secondCity: "",
      resultWay: "",
      resultDist: "Result distance: ",
      cities: [],
      add: "",
    };
  }

  componentDidMount() {
    window.addEventListener('load', this.callInit);
  }

  callInit = async () => {
    var response = await fetch('/init')
    var body = await response.json();
    this.state.cities = body;

    var firstbox = document.getElementById('first');
    var secondbox = document.getElementById('second');

    for(var i = 0; i < this.state.cities.length; i++){
      var option = this.state.cities[i];
      console.log(typeof this.state.cities[i]);
      firstbox.options.add(new Option(option, i) );
      secondbox.options.add(new Option(option, i) );
    }
  };

  searchThis = async () => {
    const response = await fetch('/', {
    method: 'POST',
    headers: {
        'Content-Type': 'application/json'
    },
    body: JSON.stringify({
        cities: {
            firstCity: this.state.firstCity,
            secondCity: this.state.secondCity
        }
      })
    });
    const body = await response.json();
    this.state.resultDist = "Result distance: " + body + " km";
    this.forceUpdate();
  };

  search(event) {
    this.state.firstCity = this.state.cities[document.getElementById("first").value]; 
    this.state.secondCity = this.state.cities[document.getElementById("second").value]; 

    this.searchThis();    
    this.forceUpdate();
  }

  addCity = async () => {
    const response = await fetch('/add_city', {
    method: 'POST',
    headers: {
        'Content-Type': 'application/json'
    },
    body: JSON.stringify({
        name: this.state.add
      })
    });
    this.callInit();
    this.forceUpdate();
  }

  addText(event) {
    this.state.add = event.target.value;
    this.forceUpdate();
  }

  render() {
    return (
      <div className="App">

        <span> From </span> <select id="first"></select> <span> to </span> <select id="second"></select>

        <br /><br /><button onClick={this.search.bind(this)}>Search</button>

        <input type="text" id="addCity" onChange={this.addText.bind(this)} />
        <br /><br /><button onClick={this.addCity.bind()}>Add city</button>

        <p className="App-intro">{this.state.resultDist}</p>

        {/* <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <h1 className="App-title">Welcome to React</h1>
        </header> */}
      </div>

    );
  }
}

export default App;
